import { createStore } from "redux";

export const calculateActionType = {
  ADD: "ADD",
  SUBTRACT: "SUBTRACT",
  DIVIDE: "DIVIDE",
  MULTIPLAY: "MULTIPLAY",
};

const initialState = {
  result: 0,
};
const calculateReducer = (state = initialState, action) => {
  switch (action.type) {
    case calculateActionType.ADD:
      return {
        ...state,
        result: state.result + action.payload,
      };
    case calculateActionType.SUBTRACT:
      return {
        ...state,
        result: state.result + action.payload,
      };

    case calculateActionType.DIVIDE:
      return {
        ...state,
        result: state.result + action.payload,
      };
    case calculateActionType.MULTIPLAY:
      return {
        ...state,
        result: state.result + action.payload,
      };
    default:
      return state;
  }
};

export const store = createStore(calculateReducer);
 store.subscribe(()=>{
    console.log("srote");
 })
