export const calculatorActionTypes = {
  ADD: "ADD",
  SUBTRACT: "SUBTRACT",
  DIVIDE: "DIVIDE",
  MULTIPLAY: "MULTIPLAY",
};

const initialState = {
  result: 0,
};
export const calculatorReducer = (state = initialState, action) => {
  switch (action.type) {
    case calculateActionTypes.ADD:
      return {
        ...state,
        result: state.result + action.payload,
      };
    case calculateActionTypes.SUBTRACT:
      return {
        ...state,
        result: state.result - action.payload,
      };

    case calculateActionTypes.DIVIDE:
      return {
        ...state,
        result: state.result / action.payload,
      };
    case calculateActionTypes.MULTIPLAY:
      return {
        ...state,
        result: state.result * action.payload,
      };
    default:
      return state;
  }
};
