import { calculateActionType, store } from '../store';
import classes from './Counter.module.css';

const Calculator = () => {
const {result}=store.getState()

const addHandler = ()=>{
  store.dispatch({type:calculateActionType.ADD,payload: 5 })
}
const divideHandler = () => {
  store.dispatch({ type: calculateActionType.DIVIDE, payload: 4 });
};

const subtractHandler = () => {
  store.dispatch({ type: calculateActionType.SUBTRACT, payload:  10});
};
const multiplayHandler = () => {
  store.dispatch({ type: calculateActionType.MULTIPLAY, payload: 2 });
};

  return (
    <main className={classes.counter}>
      <h1>Redux Calculate</h1>
      <div className={classes.value}>Result:{result}</div>
      <button onClick={addHandler}>+5</button>
      <button onClick={subtractHandler}>-10</button>
      <button onClick={multiplayHandler}>*2</button>
      <button onClick={divideHandler}>/4</button>
    </main>
  );
};

export default Calculator;
