export const authActionTypes = {
    LOG_IN:"LOG_IN",
    LOG_OUT:"LOG_OUT",
}

const initialState = {
    email:"",
    isAuthorized:false,
}
function authReducer(state=initialState,action) {
  switch (action.type) {
    case authActionTypes.LOG_IN:
      return {
        ...state,
        email: action.payload,
        isAuthorized: true,
      };
    case authActionTypes.LOG_OUT:
      return {
        ...state,
        email: action.payload,
        isAuthorized: true,
      };
    case authActionTypes.LOG_OUT:
      return initialState
      default:
        return state;
  } 

  
}

export default authReducer
